responseBody:
(function($) {


	$.fn.inflowcomponent = function(settings,parameters) {
		var windowWidth = $(window).width();


		$.fn.inflowcomponent.defaults = {
			posRight : '2',
			posTop : '100',
			mobileid : 'phoneSidebar',
			address : $("#ocsServiceUrl").val(),
			timeout : 60000,
			deviceId : windowWidth <= 580 ? 'DEVICE_PHONE' : windowWidth < 980 ? 'DEVICE_TABLET': 'DEVICE_DESKTOP'
		};





			var opts = $.extend({}, $.fn.inflowcomponent.defaults, settings);
			if ($.browser && $.browser.msie && $.browser.version <= 8.0) {
				$("#phoneSidebar, #callmeclose , #" + opts.mobileid , "#" + opts.mobilePlaceHolder).hide();
				$("#oly_2").css("width", "460px");

			}


		_.templateSettings = {
			interpolate : /\[@=([\s\S]+?)@\]/g,
			evaluate : /\[@([\s\S]+?)@\]/g,
			escape : /\[@-([\s\S]+?)@\]/g
		};


		var focusedElement = null;


		return  this.each(function(settings) {






			var isSurveyProgress = false;
			var parentEl = $(this);
			var isTouchDevice = is_touch_device();
			var ocsinflowoverlays = $('<div id="ocsinflowoverlays" class="span5"/>').appendTo(document.body);
			var options = $.extend({}, opts, $(this).data()), idletime = 0,  timestamp = new Date().getTime();
			var contextid = null, topicid = null, section = null, sectionName = null, scrolltop = null, showConsent = false, previousFocusId ="", currentVideoId= "", closedialog = false, selectedCountry = -1;
			var pageId = null;
			var phoneNo = null;
			var speechImpairedText = null;

			var inflowParameterUrl = null;
			if ($("#inflowParameterUrl").length) {
				inflowParameterUrl = $("#inflowParameterUrl").val();
			} else {
				inflowParameterUrl = options.inflowParameterUrl;
			}	


			var mobileParentEl = $('#' + options.mobileid);

			var siteId = options.siteId;

			var mobilePlaceHolderEl = $('#' + options.mobilePlaceHolder);
			var couponPlaceHolderEl = $('#' + options.couponPlaceHolder);



			var contentJson = $("#inflow-template-content").html().replace("//<![CDATA[", "").replace("//]]>", "");
			var contentJsonObj = $.parseJSON(contentJson);

			var callMETemplate = getTemplate($("#callmetemplate")), callUSTemplate = getTemplate($("#callustemplate")),  videoTemplate = null, mvideoTemplate = null; 
			var chatTemplate = getTemplate($("#chattemplate"));
			var	mobileInflowTemplate = getTemplate($("#mobile-inflow-template")), mobileInflowHelpOverLayTemplate = getTemplate($("#mobile-inflow-helpovr-template")), mobileInflowFaqOverLayTemplate = getTemplate($("#mobile-inflow-faqovr-template")), mobileInflowSecOverLayTemplate = getTemplate($("#mobile-inflow-secovr-template"));
			var callmemobileTemplate =null;
			var callusmobileTemplate = null;
			var inflowInlineSrvyTemplate = null;

			var couponTemplate = null;


			var inflowInlineFaqsTemplate = null, inflowComponentTemplate = null;

			var inactivityStep = 500;
			var inactivityPeriod = 0;
			var thresholdInactivityPeriod = -1;
			var timerId = 0;
			var isProactiveDisplayed = false;
			var channelProviderCallMe = "NONE";
			var channelProviderCallUs = "NONE";
			var channelProviderChat = "NONE";
			var surveyId="none";
			var inProgress = false;
			var myLiveHelpWin;
			var delay1 = 0;
			var delay2 = 1200;
			var propagatedEvent = false;

			if(document.location.protocol == "https:") {
				options.address = options.address.replace("http","https");
			}

			initializeTemplates();

			function initializeTemplates () {




				if ($("#inflow-inline-srvy-template").length != 0) {
					inflowInlineSrvyTemplate = getTemplate($("#inflow-inline-srvy-template"));
				}

				if ($("#videotemplate").length != 0) {
					videoTemplate = getTemplate($("#videotemplate"));
				}

				if ($("#mvideotemplate").length != 0) {
					mvideoTemplate = getTemplate($("#mvideotemplate"));
				}
				if ($("#inflow-template").length != 0) {
					inflowComponentTemplate = getTemplate($("#inflow-template"));
					//delay1= 1000;
					delay2=1200;
				} else if ($("#inflow-inline-template").length != 0) {
					inflowComponentTemplate = getTemplate($("#inflow-inline-template"));
					inflowInlineFaqsTemplate = getTemplate($("#inflow-inline-faqs-template"));
				}
				if ($("#callmemobiletemplate").length != 0) {

					callmemobileTemplate = getTemplate($("#callmemobiletemplate")); 
				}

				if ($("#inflow-coupon-template").length != 0) {

					couponTemplate = getTemplate($("#inflow-coupon-template")); 
				}


				if ($("#deviceType").val() == "DEVICE_PHONE") {
					if ($("#callusmobiletemplate").length != 0) {

						callusmobileTemplate = getTemplate($("#callusmobiletemplate"));
					}
				} else {	
					if ($("#callusmobilewithpintemplate").length != 0) {

						callusmobileTemplate = getTemplate($("#callusmobilewithpintemplate"));
					}
				}	

			}

			function getTemplate(templateElementObject) {
				var templateObjectHtml = templateElementObject.html().replace("//<![CDATA[", "").replace("//]]>", "");
				return _.template(templateObjectHtml);
			}





			var y = window.scrollY;
			window.addEventListener("orientationchange", function() {
					window.scrollTo(0,y);
			}, false);


			function setProactiveDisplayed(proactiveDisplayed) {

				isProactiveDisplayed = proactiveDisplayed;

			}


			function is_touch_device() {  
			  try {  
			    document.createEvent("TouchEvent");  
			    return true;  
			  } catch (e) {  
			    return false;  
			  }  
			}			

			var whoopcu = null, whoopcm = null, whoopchat = null;

			options.timeout = $("#ocsTimeOut").val();



			var timeoutduration = options.timeout;

			if (!options.timeout || options.timeout <= 0) {
				timeoutduration = 10000;
			}


			setInterval(function() {
				timestamp = new Date().getTime();
			}, 1200000);

			function hideFaqVideos() {
				positionFaqVideos();
				$('#i-faq').removeClass("sel");
				$("#faqdeskovr").removeClass("shw").removeClass("ovrhdn").addClass("hdn");

			}



			function hideDsktpSrvy() {
				positionFaqVideos();
				$('#i-srvy').removeClass("sel");
				$("#srvydeskovr").removeClass("shw").removeClass("ovrhdn").addClass("hdn");
				resetInflowColor();

			}

			function isSafari () {

				 return (navigator.userAgent.indexOf("Safari") > -1);

			}

			function resetInflowColor () {
				/*if (isSafari()) {

					if ($("#ocs-inflow:visible")) {
						$("#ocs-inflow").removeClass("ovrhdn").removeClass("shw");
						$("#ocs-inflow").addClass("hdn");
						setTimeout(function() {
							$("#ocs-inflow").removeClass("hdn");
							$("#ocs-inflow").addClass("ovrhdn").addClass("shw");
						},1);
					}
				}*/	
			}


			parentEl.on("blur", '#i-faq', function() {

				//resetInflowColor();
			});




			parentEl.on("blur", '#i-srvy', function() {

				resetInflowColor();
			});

			parentEl.on("click", '#i-faq', function() {
				closeSurveyAndFAQ();
				$("#i-srvy").removeClass("sel");
				if ($('#i-faq').hasClass('sel')) {

					hideFaqVideos();
				} else {
					previousFocusId = $('#i-faq');
					$('#i-faq').addClass("sel");
					$("#faqot").html(contentJsonObj.inflowcomponent.faq);
					$("#faqdeskovr").removeClass("hdn").addClass("shw");
					if ($(document).width() > 767 && mobilePlaceHolderEl.length == 0) {
						$("#faqdeskovr").addClass("ovrhdn");
					}
					setTimeout(function() {
						$("#clsfaq").focus();
					},400);	
				}

				resetInflowColor();
			});

			parentEl.on("click", '#i-srvy', function() {

				closeSurveyAndFAQ();
				$('#i-faq').removeClass("sel");
				previousFocusId = $('#i-srvy');
				if ($('#i-srvy').hasClass('sel')) {
					$('#i-srvy').removeClass('sel');

					if ($("#srvydeskovr").hasClass("shw")) {
						hideDsktpSrvy();
					}

				} else {
					startSurvey(true);

				}

			});

			parentEl.on("click", '#clsfaq', function() {
				positionFaqVideos();
				hideFaqVideos();
				$(previousFocusId).focus();
				resetInflowColor();	

			});

			parentEl.on("click", '#clssrvy', function() {
				hideDsktpSrvy();
				setTimeout(function() {
					$(previousFocusId).focus();
					resetInflowColor();
				},400);	

			});

			$("body").on("click", '#mask', function() {
				if ($("#clsvd").is(':visible')) {
					$("#clsvd").focus();
				} else if ($("#close").is(':visible')) {
					$("#close").focus();
				}
			});

			function positionFaqVideos() {
				$("#faqdeskoly").attr("style", "");
				var rightPos = 50;
				/*if (options.inflowHelpMin) {
					rightPos = 30;
				}*/



				var toppos= options.posTop;
				var winheight = $(window).height() - 70 - toppos;
				$("#faqdeskoly").css({
					"right" : (Number(options.posRight) + rightPos) + 'px',
					"top" : toppos + 'px',
					"position" : "fixed",
					"max-height" : winheight
				});
			}

			function positionSrvy() {
				$("#srvydeskolyin").attr("style", "");
				var rightPos = 50;
				/*if (options.inflowHelpMin) {
					rightPos = 30;
				}*/

				var winheight = $(window).height() - 70 - options.posTop;

				$("#srvydeskolyin").css({
					"right" : (Number(options.posRight) + rightPos) + 'px',
					"top" : options.posTop + 'px',
					"position" : "fixed",
					"max-height" : winheight
				});

			}

			function getInflowParameterURL(sectionId, topicId, contextId, isInactive) {
				var URL = inflowParameterUrl;

				if(URL.indexOf("deviceId")<0) {
					if (options.deviceId) {
						console.log("inside options.deviceid");
						URL = URL + "&deviceId=" + options.deviceId;
					}	
				}
				if (options.pageId)
					URL = URL + "&pageId=" + options.pageId;
				if (options.createdBy)
					URL = URL + "&createdBy=" + options.createdBy;
				if (options.errorId)
					URL = URL + "&errorId=" + options.errorId;
				if (options.clientId)
					URL = URL + "&clientId=" + options.clientId;
				if (options.userSegmentation)
					URL = URL + "&userSegmentation=" + options.userSegmentation;
				if (contextId)
					URL = URL + "&contextid=" + contextId;
				if (sectionId)
					URL = URL + "&sectionId=" + sectionId;
				if(isInactive)
					URL = URL + "&isInactive=" + isInactive;
				if (topicId)
					URL = URL + "&tp=" + topicId;
				if (options.categoryId)
					URL = URL + "&categoryId=" + options.categoryId;
				if (options.partId)
					URL = URL + "&partId=" + options.partId;
				if(options.trackParameters) {
					for (var key in options.trackParameters) {
						URL = URL + "&" + key +  "=" + options.trackParameters[key];
					}
				}
				return URL;
			}

			function mask() {

				if ($('#mask').length > 0) {
					return false;
				}
				var mask = $('<div id="mask" class="ioly-m fade" />').appendTo(document.body);
				mask.addClass('in');
				return true;

			}

			function showSpinnerMobile() {

				if ($('#spin').length > 0) {
					return false;
				}
				var mask = $('<div id="spin" class="infspinner fade"> <div class = "spinimg" />  </div>').appendTo(document.body);
				mask.addClass('in');
				return true;

			}


			function removeSpinnerMobile() {

				if ($('#spin').length > 0) {
					$('#spin').remove();
				}

			}


			function trackContent(documentid, isVideo) {

				var serviceArguments = "&query=" + documentid + "&isVideo=" + isVideo
						+ getInflowParameterURL(sectionName, topicid, contextid);

				var url = options.address + "/trackContent?callback=?" + serviceArguments;
				$.getJSON(url, function(data) {
					contextid = data.sessionAttributes.contextId;
				});

			}



			function setPosition() {


				if ($('#callusoverlay').hasClass("mbovly")) {
					return;
				}

				var desktopVersion = false;


				$( ".chnls" ).each(function( index ) {
					  if($( this ).hasClass("sel") &&  $("#ocsmobilenobar").val() == 'true' ) { 
						  desktopVersion = true;
					  }  

				});


				if (desktopVersion && $(window).width() < 768 ) {


				} 



				var elem = $('#callusoverlay'); 
				var oly2El= elem.find('#oly_2');
				var winEl = $(window);
				$('.oly-b').css({
					"margin-bottom" : ''
				});

				if (!desktopVersion) {

					elem.css({
						"width" : '',
						"height" : ''
					});
				}	


				var wth = oly2El.outerWidth();
				var ht = oly2El.outerHeight();
				var oW = $(window);

				if ($.browser && $.browser.msie && $.browser.version <= 8.0) {
					oly2El.css("width", "460px");
					elem.find('#callmeclose').css ({

						"display" :'none'
					});
				}	
				if (winEl.width() > 967 || desktopVersion) {
					mask();
					elem.css({
						"height" : ht,
						"width" : wth
					});
				}




				var width = elem.outerWidth(), height = elem.outerHeight(), client = {
					width : oW.width(),
					height : oW.height()
				}, top = Math.round((client.height - height) / 2), left = Math.round((client.width - width) / 2);

				if (winEl.width() > 967 || desktopVersion) {
					elem.css({
						"top" : Math.max(top, 0),
						"left" : Math.max(left, 0),
						"position" : 'fixed'

					});

				} else {

					var isovlywithclose = true;
					if (oly2El.find('#callmediv').is(':visible')) {
						isovlywithclose = false;
					}
					if ($('#' + options.mobileid).is(':visible')) {
						openCallmeOverlay(options.mobileid, isovlywithclose);
					}
				}

			}

			function callus(callustemplate,animate) {

				var serviceArguments = getInflowParameterURL(sectionName, topicid, contextid);
				var url = options.address + "/callus?callback=?" + serviceArguments + "&channelProvider=" + channelProviderCallUs + "&overlayType=-1";



				$.getJSON(url, function(data) {
					data.content = contentJsonObj;
					if ($('#callusoverlay').is(':visible')) {
						return;
					}
					if (!(data.hasOwnProperty("genEWT")) ){
						data.genEWT = null;
					}


					if (data.overlayType == 0) {

						//if (!($.browser.msie && $.browser.version < 10.0)) {
						if (window.history && window.history.pushState) {
							 history.replaceState("selcallus", "selcallus", "selcallus");
							 history.pushState("callus", "callus", "callus");
						 }
					}
					if (callustemplate == null || data.overlayType ==0) { 
						$.when($("#callusoverlay").remove()).then(populateCallUSOverlay(data,callustemplate,animate));
					} 	 else {
						if ($("#deviceType").val() == "DEVICE_PHONE") {
							window.location.href = "tel:"+ data.phoneNo;
						} else {
							$.when($("#callusoverlay").remove()).then(populateCallUSOverlay(data,callustemplate,animate));
						}
					}
				});

			}


			function clickcallus(submitwophno) {
				if (submitwophno) {

					var serviceArguments = getInflowParameterURL(sectionName, topicid, contextid);
					var url = options.address + "/callus?callback=?" + serviceArguments + "&channelProvider=" + channelProviderCallUs + "&overlayType=1" + "&withoutPhNo=true&submitPOTP=true";
					console.log (url);
					window.location.href = "tel:"+ $("#cmphno").val();
					$.getJSON(url);


				} else {
					var phoneNumber = $('#cmprimaryphno').val();
					var serviceArguments = getInflowParameterURL(sectionName, topicid, contextid);
					var url = options.address + "/callus?callback=?" + serviceArguments + "&channelProvider=" + channelProviderCallUs + "&overlayType=1" + "&phoneNumber=" + phoneNumber + "&selectedCountry=" + selectedCountry + "&submitPOTP=true";
					$.getJSON(url, function(data) {
						if (data.status == 1) {
						$("#infpherror").addClass("hdn");	
						window.location.href = "tel:"+ $("#cmphno").val();
					}  else {
						$("#infpherror").removeClass("hdn");
					}
					});
				}	

			}

			var currentSurvey;
			function startSurvey(isDesktop) {

				var serviceArguments = getInflowParameterURL(sectionName, topicid, contextid);
				var url = options.address + "/startSurvey?callback=?" + serviceArguments + "&query=" + surveyId;

				if (!isDesktop) {

					showSpinnerMobile();
				}

				if (isSurveyProgress == false) {
					isSurveyProgress = true;
					$.getJSON(url, function(data) {
						isSurveyProgress = false;
						data.content = contentJsonObj;
						if (data.status ==1) {
							surveyid =  data.surveyContent.survey.id;
						}	
						currentSurvey = data;
						removeSpinnerMobile();
						$.when($("#surveycontentovly").remove()).then(populateSurveyOverlay(data,isDesktop));
					});
				}

			}


			function populateSurveyOverlay(data, isDesktop) {

				if (isDesktop==true) {
					data.isDesktop = true;
					var temp = $(inflowInlineSrvyTemplate(data));
					$.when($("#srvy-vid").empty()).then($(inflowInlineSrvyTemplate(data)).appendTo($("#srvy-vid")));

					positionSrvy();

					previousFocusId = $('#i-srvy');
					$('#i-srvy').addClass("sel");

					if (options.surveyTitle) {
						$("#srvyot").html(options.surveyTitle);
					} else {

						$("#srvyot").html(contentJsonObj.inflowcomponent.technicalissues);
					}	
					$("#lgl").find ("a").attr("target","_blank");
					$("#thanksdsk").find ("a").attr("target","_blank");


					$("#srvydeskovr").removeClass("hdn").addClass("shw");
					if ($(document).width() > 767 && mobilePlaceHolderEl.length == 0) {

						$("#srvydeskovr").addClass("ovrhdn");
					}
					setTimeout(function() {
						$("#clssrvy").focus();
					},400);
					resetInflowColor();


				} else {	
					data.isDesktop = false;
					$(inflowInlineSrvyTemplate(data)).appendTo($("#srvyenclose"));
					openMobileSurveyOverlay(options.mobileid);
					setTimeout(function() {
						$("#mclssurvey").focus();
					},1000);
				}	

			}

			parentEl.on('keydown', '#clssrvy', function(event) {

				if(event.shiftKey && event.keyCode == 9) { 
					event.preventDefault();
				}
			});


			function populateCallUSOverlay(data, callustemplate, animate) {



				var callusolvy;
				data.channel = "callus";
				data.pageId = pageId;
				data.phoneNo = phoneNo;
				data.speechImpairedText = speechImpairedText;
				data.siteId = siteId;
				if (animate) {

					removeSpinnerMobile();
				}
				//data.supportedInflowCountriesEnum = data.supportedCountriesEnum;
				if (pageId == 'ResCenterHome') {
					data.tempPIN = null;
				}
				if (callustemplate == null) {
					$(callUSTemplate(data)).appendTo(ocsinflowoverlays);
					 callusolvy = $('#callusoverlay');


						$('#dragger').dragger({
							"target" : callusolvy
						});

					callusolvy.find('#phno').text(data.phoneNo);



					if (data.tempPIN) {
						callusolvy.find('#pscd').text(data.tempPIN);
						callusolvy.find('#pin').removeClass('hdn');
					}
					if (data.expiryTime) {
						callusolvy.find('#exp').text(data.expiryTime);
						callusolvy.find('#pinexpire').removeClass('hdn');

					}

					if (data.genEWT !=null) {
						callusolvy.find('#wait').text(data.genEWT);

					}


				} else {

					if ($("#deviceType").val() == "DEVICE_PHONE") {
						$(callustemplate(data)).appendTo(ocsinflowoverlays);
						callusolvy = $('#callusoverlay');
						waitTime = data.waitTime;
						cutopicName = data.topicName;
					} else {
						$(callustemplate(data)).appendTo(ocsinflowoverlays);
						callusolvy = $('#callusoverlay');
						callusolvy.find('#phno').text(data.phoneNo);
						if (data.tempPIN) {
							callusolvy.find('#pscd').text(data.tempPIN);
							callusolvy.find('#pin').removeClass('hdn');
						}
						if (data.expiryTime) {
							callusolvy.find('#exp').text(data.expiryTime);
							callusolvy.find('#pinexpire').removeClass('hdn');

						}

						if (data.genEWT !=null) {
							callusolvy.find('#wait').text(data.genEWT);

						}
					}	
				}	

				if (!mask()) {
					return;
				}

				var hoopel =  $('#hoop');
				var spitxtel =  $('#spitxt');

				if (data.callUsOverlayText) {
					callusolvy.find(hoopel).html(data.callUsOverlayText).removeClass('hdn');
				}
				if (data.speechImpairedText) {
					callusolvy.find(spitxtel).text(data.speechImpairedText).removeClass('hdn');
				}
				$(".whoopcu").html(whoopcu);
				callusolvy.find('#callustitle').removeClass('hdn');
				callusolvy.removeClass('hdn');
				callusolvy.find('#callusdiv').removeClass('hdn');

				if (data.overlayType == 0) {

					var countrylist = data.supportedInflowCountriesEnum;
					selectedCountry = data.selectedCountry;


					$.each(countrylist, function(key1, val) {
						var splited = key1.split("=");
						var countryId = splited[1];
						if (selectedCountry == countryId) {
							callusolvy.find('.landline').text(val.landLineTip);
							callusolvy.find('.mobile').text(val.cellTip);
							return;
						}
					});
				}

				if (animate) {
					var width = $(window).width();
					$('#callusoverlay').css({
						"left" : width,
						"position" : 'fixed',
						"top" : 0
					});
					$('#callusoverlay').removeClass("hdn");
					$('#callusoverlay').animate({'left': '0px'}, 200);
				}	
				setPosition();
				if($(window).width() > 967) {
					//if ($.browser.msie) {
					if (navigator.userAgent.indexOf("MSIE") != -1) {
						callusolvy.find('#oly_2').focus();
					} else {
						callusolvy.find('#close').focus();
					}
				}	

				if ($("#deviceType").val() == "DEVICE_PHONE") {

					callusolvy.find("#pin").addClass("hdn");
					callusolvy.find("#pinexpire").addClass("hdn");
				}

			}


			function showCallMeInvalidPhone() {
				$('#error').removeClass("hdn");
				$('#cmphno').attr("aria-labelledby","error").focus();
			}

			function clickcallme(callmeTemplate) {

				var phoneNumber = $('#cmphno').val();
				var isConsent = false;
				if (showConsent) {
					if ($("input:radio[name=oconsent]:checked").val() != 'yes') {

						isConsent = false;
					} else {

						isConsent = true;
					}

				}
