			function populateCallMeOverlay(data,callmeTemplate, animate) {



				if (animate) {

					removeSpinnerMobile();
				}
				if (callmeTemplate == null) {
					$(callMETemplate(data)).appendTo(ocsinflowoverlays);
				} else {

					$(callmeTemplate(data)).appendTo(ocsinflowoverlays);
				}	
				var callusovly= $('#callusoverlay');


				$('#dragger').dragger({
					"target" : callusovly
				});

				callusovly.find(".whoopcm").html(whoopcm);
				if (!mask()) {
					return;
				}
				contextid = data.sessionAttributes.contextId;
				if (data.status == 2) {
					refreshInflow();
					setPosition();
					return;
				}

				if (data.callMeChannelStateEnum.id == 4) {
					callusovly.find('#cmtopicname').text(data.topicName);
					callusovly.find("#wait").text(data.waitTime);
				} else if (data.callMeChannelStateEnum.id == 0 || data.callMeChannelStateEnum.id == 2) {
					$("#sel_cntry").css("height", $("#cmphno").outerHeight());

					callusovly.find("#phtitle").find("#cmtphno").replaceWith(data.phoneNo);
					callusovly.find("#cmphno").attr("title", callusovly.find("#phtitle").text());

					callusovly.find("#tpname").text(data.topicName);
					if (data.waitTime>=0) {
						callusovly.find("#wait").text(data.waitTime);
					}
					callusovly.find("#cmtphno").val(data.phoneNo);


					var countrylist = data.supportedInflowCountriesEnum;
					selectedCountry = data.selectedCountry;

					waitTime = data.waitTime;
					cutopicName = data.topicName;

					$.each(countrylist, function(key, val) {
						var splited = key.split("=");
						var countryName = splited.length == 3 ? splited[2] : splited[0];
						var countryId = splited[1];
						if (selectedCountry == countryId) {
							callusovly.find("#span_cntry").text(countryName + " +" + val.countryCode);
							callusovly.find('.landline').text(val.landLineTip);
							callusovly.find('.mobile').text(val.cellTip);
							return;
						}
					});

				}

				if (animate) {
					var width = $(window).width();
					$('#callusoverlay').css({
						"left" : width,
						"position" : 'fixed',
						"top" : 0
					});
					$('#callusoverlay').removeClass("hdn");
					$('#callusoverlay').animate({'left': '0px'}, 200);
				}	
				setPosition();
				if($(window).width()>967) {
					//if ($.browser.msie) {
					if (navigator.userAgent.indexOf("MSIE") != -1) {
						$('#oly_2').focus();
					} else {
						$('#close').focus();
					}
				}	
			}

