			function infscallback (data) {

				var section;
				var prop;

				for (prop in data.responseMap) {
					section = data.responseMap[prop];
				}

				if (!section) {
					return;
				}
				var channelRecords = section.channelRecords;
				var surveyRecords = section.surveyRecords;
				var channelRecordsArray = new Array(6);
