				if (section.faqRecords) {
					section.faqrecords = section.faqRecords;
				}
				var faqRecords = section.faqrecords;
				var lookupRecord = null;
				var preferredChannelRecords = section.preferredChannelRecords;
				var callusChannelRecord = null, chatChannelRecord = null, callmeChannelRecord= null;


