				pageId = section.pageId;
				var j =0;
				var faqlen = faqRecords.length; 
				for(i=0;i<faqlen ;i++) {

					if(faqRecords[j].hasOwnProperty("status")) {
						if(faqRecords[j].status == 6) {
							faqRecords.splice(j,1);
						} else {

							j = j+1;
						}
					}	
				}


				section.faqrecords = faqRecords;

				if (faqRecords.length == 0) {

					return;
				}
				$("#chatpopupovr").addClass("hdn").removeClass("shw");;

				data.islink = options.isMobileLink;

				var videorecords = section.videoRecords;
				j = 0;
				var vidlen = videorecords.length;
				for ( var i = 0; i < vidlen; i++) {
					if (videorecords[j].hasOwnProperty("status")) {
						if(videorecords[j].status == 6) {
							videorecords.splice(j,1);
							continue;
						} else {

							j =j+1;
						}
					}	
				}

				section.videoRecords = videorecords;


				for (var lookupRecord in lookupRecords ) {

					if(options.deviceId !== "DEVICE_PHONE") {
						thresholdInactivityPeriod = lookupRecords[lookupRecord].value;
						timerId = setInterval(increment, inactivityStep);
					}	
				}


				var i=0;
				var numChannels = 0;





				var j =0;
				var srvylen = surveyRecords.length; 
				for(i=0;i<srvylen ;i++) {

					if(surveyRecords[j].hasOwnProperty("status")) {
						if(surveyRecords[j].status == 6) {
							surveyRecords.splice(j,1);
						} else {

							j = j+1;
						}
					}	
				}


				section.surveyRecords =  surveyRecords


				for(i=0;i<channelRecords.length;i++) {
					if(!(channelRecords[i].status ==6)) {

						numChannels = numChannels + 1;

						if(channelRecords[i].channelType==="LIVE_HELP" || channelRecords[i].channelType==="CHAT") {
								channelProviderChat = channelRecords[i].channelProvider;
								if(channelRecords[i].available) {
									channelRecordsArray[0] = channelRecords[i];
								} else {
									channelRecordsArray[3] = channelRecords[i];

								}
								chatChannelRecord =  channelRecords[i];

						}

						else if(channelRecords[i].channelType==="CALL_US") {
							channelProviderCallUs = channelRecords[i].channelProvider;
							if(channelRecords[i].available) {
								channelRecordsArray[1] = channelRecords[i];
							} else {
								channelRecordsArray[4] = channelRecords[i];

							} 
							callusChannelRecord =  channelRecords[i];
							phoneNo = channelRecords[i].phoneNumber;
							speechImpairedText = channelRecords[i].speechImpairedText;
						}

						else if(channelRecords[i].channelType==="CALL_ME") {
							channelProviderCallMe = channelRecords[i].channelProvider;
							if(channelRecords[i].available) {
								channelRecordsArray[2] = channelRecords[i];

							} else {
								channelRecordsArray[5] = channelRecords[i];

							} 

							callmeChannelRecord =  channelRecords[i];

						}
					}

				}
				if (numChannels==0 && options.hideFAQWhenNoChannels) {

