					$(".videoiconhr").css({"display" : "none"});


						$.when($(".mobile-faqovr").remove()).then($(mobileInflowHelpOverLayTemplate(data)).appendTo($("#faqenclose")));
						$.when($("#seccontent").remove()).then($(mobileInflowSecOverLayTemplate(data)).appendTo($(".mfaqttlenclose")));
						for ( var i = 0; i < seclen && seclen > 1 ; i++) {

							var inflowstr = $($('div[inflow]').get(i)).attr('inflow');
							if(inflowstr!=null && inflowstr.length!=0) {
