									temp = $('.sl').first().clone();
									$(temp).find('.sectionTitle').attr('tabindex',i+1);
									$(temp).appendTo('#sectionlist');
								}
								var jsonobj = $.parseJSON(inflowstr);
								if (jsonobj.property_name == "sectionId") {
									var sttl = jsonobj.section_title;
									$(temp).find('.sectionTitle').html(sttl).attr('inflow', inflowstr);
								}
							}
						}




				} else {

					$.when($(".mobile-faqovr").remove()).then($(mobileInflowHelpOverLayTemplate(data)).appendTo($("#faqenclose")));
					$.when($("#infcontent").remove()).then($(mobileInflowFaqOverLayTemplate(data)).appendTo($(".mfaqttlenclose")));

				}


				/*if ($("#sectionlist a").length > 0) {
					$("#secfaqenclose").find(".mfaqttlenclose").remove();
					$(".mfaqttlenclose").appendTo($("#secfaqenclose"));
				}*/


				if (inflowInlineFaqsTemplate) {
					$.when($("#faq-vid").empty()).then($(inflowInlineFaqsTemplate(data)).appendTo($("#faq-vid")));


					if (openFaqOverlay) {
						$('#i-faq').click();
					} else {
						positionFaqVideos();
					}
				}

				$.each(data.responseMap, function(key, val) {
					section = val;
					sectionName = key;
				});

				populateVideos(videorecords, [ ('.infhelp'), ('#ovlyfaq'), ('#faq-vid') ]);
				contextid = section.contextId;
				topicid = section.topicId;

				for ( var i in section.channelRecords) {
					var channelRecord = section.channelRecords[i];
					var channelType = channelRecord.channelType;
					if (channelType == 'CALL_US' && channelRecord.hooptext) {
						whoopcu = channelRecord.hooptext;
					} else if (channelType == 'CALL_ME' && channelRecord.hooptext) {
						whoopcm = channelRecord.hooptext;

					} else if ((channelType == 'LIVE_HELP' || channelType == 'CHAT' ) && channelRecord.hooptext) {
						whoopchat = channelRecord.hooptext;

					}
				}



				for (var preferredChannelRecord in preferredChannelRecords ) {


					var preferredChannel = preferredChannelRecords[preferredChannelRecord].value;
					openProactiveChatOvly(preferredChannel, channelRecordsArray);

				}

				populateMobileCallusAndChat(callusChannelRecord, chatChannelRecord, callmeChannelRecord );




				for(i=0;i<surveyRecords.length;i++) {
					if(surveyRecords[i].hasOwnProperty("status")) {
						if (surveyRecords[i].status == 6) {
							break;
						} 

					}	

					surveyId = surveyRecords[i].surveyId;

					if (options.surveyTitle) {
						$("#msurveylnk").html(options.surveyTitle);
					} 
					$("#msurvey").removeClass("hdn");
