			function callInflowService(serviceArguments, isHandleSelect) {

				if (inProgress == true ){

					return;
				}
				inProgress = true;
				var infrescallback = "infscallback";
				if (isHandleSelect) {

					infrescallback = "handleSelectCallback";
				}


				var url = options.address + "/single?" + serviceArguments + "&timestamp=" + timestamp;

				var deferred = $.ajax({
					url : url,
					crossDomain : true,
					cache : true,
					dataType : "jsonp",
					jsonpCallback : infrescallback,
					timeout : timeoutduration,
					success: function(data) { 
						 	if (isHandleSelect) {
						 		handleSelectCallback(data);
						 	} else {
						 		infscallback(data);
						 	}	
				    }
				});


				deferred.always(function() {

					inProgress = false;
					if (typeof options.initcallback == 'function') {
						options.initcallback.call();
					}



				});

			}

