					collapseFAQ(this);
				});
				$(".mexpand").each(function() {
					collapseFAQ(this);
				});
			}

			function collapseFAQ(collapse) {
				var parent = $(collapse).parents('.activefaq');

				if ($(collapse).hasClass("mcollapse") ||  $(collapse).hasClass("mexpand")) {
					parent.find('.mquestion').removeClass('expanded');
					parent.find('.mquestion').attr("aria-expanded","false");
				} else {
					parent.find('.question').removeClass('expanded');
					parent.find('.question').attr("aria-expanded","false");
				}	
				parent.find('.answer').addClass('hdn');
				parent.find('.expand').removeClass('hdn');
				parent.find('.expand').css ({"display":"inline-block"});
				parent.find('.collapse').addClass('hdn');
				parent.find('.collapse').css ({"display":"none"});
				parent.find('.mexpand').removeClass('hdn');
				parent.find('.mcollapse').addClass('hdn');
			}

			function expandFAQ(expand) {

				collapseAll();
				$(expand).attr("aria-expanded","true");
				var documentid;
				var parent = $(expand).parents('.activefaq');

				if ($(expand).hasClass("mexpand") || $(expand).hasClass("mcollapse")) {
					documentid = parent.find('.mquestion').attr("documentid");
					parent.find('.mquestion').addClass('expanded');
				} else {
					documentid = parent.find('.question').attr("documentid");
					parent.find('.question').addClass('expanded');
				}	

				parent.find('.answer').removeClass('hdn');
				parent.find('.expand').addClass('hdn');
				parent.find('.collapse').removeClass('hdn');
				parent.find('.collapse').css ({"display":"inline-block"});
				parent.find('.expand').css ({"display":"none"});
				parent.find('.mexpand').addClass('hdn');
				parent.find('.mcollapse').removeClass('hdn');
				parent.find('.db').height(parent.find('.enclose').height());
				trackContent(documentid, false);
			}


			parentEl.on("click", '.collapse', function(event) {
				collapseFAQ(event.target);
			});

			parentEl.on("click", '.expand', function(event) {
				expandFAQ(event.target);
			});

			parentEl.on("click", '.mactivefaq', function(event) {

				if (($(this).find('.mexpand')).hasClass('hdn')) {
					collapseFAQ($(this).find('.mcollapse'));
				} else {
					expandFAQ($(this).find('.mexpand'));
				}	 
			});


			parentEl.on("click", '.question', function(event) {
				var question = event.target;
				var parent = $(question).parents('.activefaq');
				if (parent.find('.answer').hasClass('hdn')) {
					expandFAQ(event.target);
				} else {
					collapseFAQ(event.target);
				}


			});


			function positionMobileVideo () {

				var videoOverlay = $('#videooverlay'), video = videoOverlay.find("iframe") , oW = $(window);

				video.width(oW.width() - 45);
				if (oW.width() <= 600) {
					video.css({
						"height" : 'auto'
					});
				} else {

					video.css({
						"height" : '200'
					});
				}
				$("#mvcancel").html(contentJsonObj.inflowcomponent.cancel);
			}


			function positionVideo() {
				var videoOverlay = $('#videooverlay'), video = videoOverlay.find("iframe"), innerOverLay = videoOverlay.find("#voly_2"), oW = $(window);

				if(($.browser && $.browser.msie && $.browser.version <= 8.0)){
					$('#vdmclose').hide();
				}

				if (oW.width() <= 650){

					video.width(oW.width() - 45);
					if (oW.width() <= 600) {
						video.css({
							"height" : 'auto'
						});
					} else {

						video.css({
							"height" : '200'
						});
						innerOverLay.css({
							"top" : '0'
						});
					}
					videoOverlay.removeClass('hdn').css({
						"height" : '100%',
						"width" : '100%',
						"z-index" : '9999999',
						"position" : 'fixed'
					});
					videoOverlay.find('.closebar').css ({"display":''});
				} else {
					video.width(460).height(300);
					videoOverlay.find('.closebar').css ({"display":'none'});
					videoOverlay.removeClass('hdn').css({
						"height" : innerOverLay.outerHeight(),
						"width" : 500,
						"z-index" : '2002'
					});
					innerOverLay.css({
						"padding" : 20,
						"background-color" : '#FFFFFF',
						"z-index" : '2002'
					});
				}

				var width = videoOverlay.outerWidth(), height = videoOverlay.outerHeight(), client = {
					width : oW.width(),
					height : oW.height()
				};

				var top = Math.round((client.height - height) / 2), left = Math.round((client.width - width) / 2);

				videoOverlay.css({
					"top" : Math.max(top, 0),
					"left" : Math.max(left, 0),
					"position" : 'fixed'
				});


				$("#mvcancel").html(contentJsonObj.inflowcomponent.cancel);

			}


