			function checkSurveyAnswered() {


				var isAnswered = false;
				var questions = currentSurvey.surveyContent.survey.questions;


			    for (i=0;i<questions.length;i++) 
			    {
			    	var type = questions[i].type;
			    	var questionid = questions[i].qid;
			    	questionid = "_" + questionid;
			    	if (type === "single-select" || type === "ratings") {
			    		$.each($('.' + questionid +  ':checked'), function(){
			    			 isAnswered = true;

				         });
			    	}	
			    	else if (type === "multiple-select") {


			    		 $.each($('.' + questionid +  ':checked'), function(){
			    			 isAnswered = true;
				         });


			    	}

			    	else if (type === "textarea" || type === "text") {
			    		var value = $('#' + questionid).val();
			    		var maxlength = $('#' + questionid).attr("maxlength");
			    		var charleft =  maxlength - value.length;
			    		$('#max' + questionid).text(charleft);

			    		if (value != null && value != "undefined" && value != "") {
			    			 isAnswered = true;

			    		}

			    	}
			    }
			    if (isAnswered) {
			    	$("#sbtsurvey").removeAttr("disabled","false");
			    	$("#sbtsurvey").attr("aria-disabled","false");
			    	$("#sbtsurvey").removeClass("btnd");
			    } else {

			    	$("#sbtsurvey").attr("disabled","true");
			    	$("#sbtsurvey").attr("aria-disabled","true");
			    	$("#sbtsurvey").addClass("btnd");
			    }

			} 


