			function submitSurvey () {

				var questions = currentSurvey.surveyContent.survey.questions;

				var surveyjson  = {};
			    for (i=0;i<questions.length;i++) 
			    {
			    	var type = questions[i].type;
			    	var questionid = questions[i].qid;
			    	questionid = "_" + questionid;
			    	if (type === "single-select" || type === "ratings") {
			    		var checkedValue = $('.' + questionid +  ':checked').val();
			    		surveyjson[questionid] = checkedValue;
			    	}	

			    	if (type === "multiple-select") {
			    		var values = [];

			    		 $.each($('.' + questionid +  ':checked'), function(){
			    			 values.push($(this).val());
				         });

			    			 surveyjson[questionid] = values;

			    	}

			    	if (type === "textarea" || type === "text") {
			    		var value = $('#' + questionid).val();
			    		surveyjson[questionid]= value;
			    	}
			    }

			    var surveytext = JSON.stringify(surveyjson);
			    surveytext =  encodeURIComponent(surveytext);
			    var serviceArguments = getInflowParameterURL(sectionName, topicid, contextid);

			    var url = options.address + "/submitSurvey?callback=?" + serviceArguments +   "&query=" + surveyId + "&surveyId=" +  surveyid + "&respInpText=" + surveytext;
				$.getJSON(url, function(data) {

				});
			}


