			function increment () {



				if(thresholdInactivityPeriod <=0) {
					return;
				}
				if (!$("#chatpopupovr").hasClass("hdn")) {
					inactivityPeriod = 0;
					return;
				}
				inactivityPeriod = inactivityPeriod + inactivityStep;
				if(inactivityPeriod > thresholdInactivityPeriod*1000) {
					inactivityPeriod = 0;
					var channelslist =$(".chnls"); 
					var isActive=false;
					$.each(channelslist,function(index,obj) {
						  if($(obj).hasClass("sel") ) { 
								isActive = true;
						  }  
					});
					if(isActive || ($('#mask').length > 0)) {

						return;
					}
					if ($(window).width() > 967 && !isProactiveDisplayed) {
						clearInterval(timerId);
						setProactiveDisplayed(true);
						focusedElement= $("*:focus");
						callProactiveInflowHelp();
					}	
				}
			}

			function getChannelNumber (channel, channelRecords) {

				var channelNumber = 0;
				for (var i in channelRecords) {

					if(channelRecords[i].channelType === "CALL_US") {
						channelNumber = channelNumber + 1; 
						if(channel.toLowerCase() === "callus") {
							if(channelRecords[i].available) {
								return (channelNumber);
							} 
							return -1;
						}

					} else if(channelRecords[i].channelType === "CALL_ME") {
						channelNumber = channelNumber + 1;
						if(channel.toLowerCase() === "callme") {

							if(channelRecords[i].available) {
								return (channelNumber);
							} 
							return -1;
						}

					}

					else if(channelRecords[i].channelType === "LIVE_HELP" || channelRecords[i].channelType === "CHAT") {
						channelNumber = channelNumber + 1;
						if(channel.toLowerCase() === "chat") {

							if(channelRecords[i].available) {
								return (channelNumber);
							} 
							return -1;
						}

					}
				}

				return -1;	
			}


			function openProactiveChatOvly(channel, channelRecords) {
				var top = 50;
				var channelNumber = getChannelNumber(channel, channelRecords);
				if(channelNumber < 0 ) {

					return;
				}
				top = top * channelNumber ;
				if($("#chatpopupovr").hasClass("hdn")) {
					if(channel.toLowerCase()=== "chat")  {
						$("#chatwus").text(contentJsonObj.inflowcomponent.chatwithus);
						$("#chatwus").attr("channelid","i-chat");

					} else if(channel.toLowerCase() === "callus")  {
