var t24App = angular.module('t24', ['ui.router','ngAnimate','ui.bootstrap','ngStorage','ngCookies','ngMap','ngGeolocation']);
t24App.run(function($rootScope,$window,$state){
    $rootScope.$state = $state;
    $rootScope.goBack = function(){
        $window.history.back();
    };
    $rootScope.$on("$routeChangeSuccess", function (event, currentRoute, previousRoute) {
        window.scrollTo(0, 0);
    });
});
