'use strict';

t24App.directive('topNavRecharge',function(){
		return {

	        templateUrl:'scripts/directive/topnav/topnavrecharge.html',
	        controller: function($scope){
	        	var selector, elems, makeActive;

				selector = '.mobile-payment-nav .container ul li';

				elems = document.querySelectorAll(selector);

				makeActive = function (e) {
					e.preventDefault();
				    for (var i = 0; i < elems.length; i++)
				       {
				        elems[i].classList.remove('active');
				        var id = elems[i].id;
				        var img = document.getElementById(id).getElementsByTagName('*')[1];
				        img.src = "images/"+id+"1.png";
				       }

				    this.classList.add('active');
				    this.getElementsByTagName('*')[1].src = "images/"+this.id+"-nav-a.png";
				};

				for (var i = 0; i < elems.length; i++){
					elems[i].addEventListener('mousedown', makeActive);
				}


	        }	       
    	}
	});
