t24App.controller('homeCtrl', ['$scope', '$rootScope', 'authService', '$cookieStore', 'appService', '$stateParams', '$state', '$log',function($scope, $rootScope, authService, $cookieStore, appService, $stateParams, $state, $log) {
    //console.log('homeCtrl');
    //console.log(authService);
    $('.carousel').carousel();
 
    $scope.getAppTokenNumber = function() {
        var registeredID = "t24web";
        var userId = "webuser";
        var password = "A7A84EF8FDF90A1F353515DB106F1955B561C170932292663EF2D4A4F6915336198048900B0E99BE";
        authService.getAppToken(registeredID, userId, password, function(response) {
            $log.log('appToken-->',response.data.appToken);
            $rootScope.appToken = response.data.appToken;
            $cookieStore.put('appToken', response.data.appToken) //authService.setCredentials(response.data.appToken);

            //var appToken = $cookieStore.get('appToken');
            appService.getBanner('PROMOBANNER',$rootScope.appToken,function(res){
                if (res.isSuccessful == false) {
                    //$log.log(res.msgDesc);
                } else {
                    //$log.log('Banner',res);
                    $scope.bannerSet = res.data;
                }
            });
        });
    };
    $scope.getAppTokenNumber();


    $scope.loginerrorrMsg = false;
    $scope.errorrMsg = false;
    $scope.rechargeNow = function() {
        //console.log("clicked");
        var appToken = $cookieStore.get('appToken');

        appService.verifyT24(appToken, $scope.form.mobileNo, function(response) {
            //	console.log(response);
            if (response.isSuccessful == false) {
                $scope.errorrMsg = true;
            } else {
                $state.go('twog', {});
            }
        });
    };

}]);
