t24App.controller('accountsCtrl', ['$scope','appService','authService','$log','$cookieStore','$window','$state', function($scope,appService,authService,$log,$cookieStore,$window,$state){
	console.log('accountsCtrl' ,$cookieStore.get('globals').currentUser.registeredID);
	var appToken = $cookieStore.get('appToken');
	var mobileNo = $cookieStore.get('globals').currentUser.registeredID;
	$scope.loggedIn = $cookieStore.get('userloggedIn');
	var s =  $cookieStore.get('invisible');
    $scope.invisible = s;

	$scope.loadCustomerAccount = function(){
	console.log('accountsCtrl' ,$cookieStore.get('globals').currentUser.registeredID);

		//appService.getProfile("8976595947","C1D048FA84B21D197B0AA67A536DD2E49C54321D79A832304BC7403A9E23F3AB791D92BBA038462B",function(response){
		appService.getProfile($cookieStore.get('globals').currentUser.registeredID,appToken,function(response){
			console.log(response);


			appService.getCustomerAcc(response.data.contactNo,response.data.custToken,function(response){
				$log.info(response);
				if(response.data != null){
				$scope.customerAccDetails = response.data['Balance Info : '];
			}else if (response.isSuccessful == false && response.msgDesc == 'Token is Not Valid') {
                //$log.log(response.msgDesc);
                if ($scope.loggedIn == 1) {
                    $scope.invisible = !s; //!authService.invisible;
                    $cookieStore.put('invisible', !s);
                    $cookieStore.put('userloggedIn', 0);
                    authService.clearCredentials();
                    $window.alert('Session Expired ! Please login again.');
                    $state.go('home');

                } else {
                    $window.alert('Session Expired ! Click OK to start session again');
                    $state.go('home');
                }
            }else{
				$scope.customerAccDetails = 0;
			}
				
				//$log.info($scope.customerAccDetails);
			});
		});
	};

	$scope.loadCustomerAccount();
}]);
