t24App.controller('rechargeCtrl', ['$scope','appService','$log','$stateParams','$state','$window','$cookieStore', function($scope,appService,$log,$stateParams,$state,$window,$cookieStore){
	console.log('accountsCtrl');

  $scope.loggedIn = $cookieStore.get('userloggedIn');
  var s =  $cookieStore.get('invisible');
    $scope.invisible = s;
  
  console.log($stateParams.mobileNo);
  $scope.loadGetStvList = function(){
    appService.getStvList('F3E630A9AEAAC2CB186516AB018ACFB1A1BE7902C0BAFB48194E1CE8260F1A73F676CBC063E52B80','T24',function(response){
      $log.info(response.data);
      //$scope.stvList = response.data;
      $scope.categoryComboPacksArr = [];
      $scope.categoryDataPacksArr = [];
      $scope.categoryFullTalktimeArr = [];
      $scope.categoryRoamingArr = [];
      $scope.categorySpecialRechargeArr = [];
      $scope.categoryTopupArr = [];
      $scope.categoryOthersArr = [];
      angular.forEach(response.data, function(value, key){
         console.log(value.category);        
          if (value.category == 'ComboPacks') {
              $scope.categoryComboPacksArr.push(value);
          }
          else if (value.category == 'DataPacks') {
              $scope.categoryDataPacksArr.push(value);
          }
          else if (value.category == 'FullTalktime ') {
              $scope.categoryFullTalktimeArr.push(value);
          }
          else if (value.category == 'Roaming') {
              $scope.categoryRoamingArr.push(value);
          }
          else if (value.category == 'SpecialRecharge') {
              $scope.categorySpecialRechargeArr.push(value);
          }
          else if (value.category == 'Top-up') {
              $scope.categoryTopupArr.push(value);
          }
          else{
              $scope.categoryOthersArr.push(value);
          }
      });
      
    });	
  };
  $scope.loadGetStvList();

  $scope.getAmount = function(amount){
    console.log(amount);
    $state.go('verifyPay', {'mobileNo':$stateParams.mobileNo,'email':$stateParams.email,'amount':amount});
  };

}]);
