t24App.controller('getConnectionCtrl', ['$scope','$window','$timeout', 'appService', 'authService','$log', '$cookieStore','$state', function($scope, $window, $timeout,appService, authService,$log, $cookieStore,$state) {
    console.log('getConnectionCtrl');
    var appToken = $cookieStore.get('appToken');
    $scope.loggedIn = $cookieStore.get('userloggedIn');
    var s =  $cookieStore.get('invisible');
    $scope.invisible = s;

    $scope.loader = false;
    $scope.successMsg = false;
    $scope.errorloader = false;

    $scope.allCircles = [
    {'id':'CIR001','name':'Delhi'},
    {'id':'CIR002','name':'Mumbai'},
    {'id':'CIR003','name':'Chennai'},
    {'id':'CIR004','name':'Kolkata'},
    {'id':'CIR005','name':'Maharashtra'},
    {'id':'CIR006','name':'Gujarat'},
    {'id':'CIR007','name':'Andhra Pradesh'},
    {'id':'CIR008','name':'Karnataka'},
    {'id':'CIR009','name':'Tamil Nadu'},
    {'id':'CIR010','name':'Kerala'},
    {'id':'CIR011','name':'Punjab'},
    {'id':'CIR012','name':'Haryana'},
    {'id':'CIR013','name':'Uttar Pradesh (West)'},
    {'id':'CIR014','name':'Uttar Pradesh (East)'},
    {'id':'CIR015','name':'Rajasthan'},
    {'id':'CIR016','name':'Madhya Pradesh'},
    {'id':'CIR017','name':'West Bengal& AN Island'},
    {'id':'CIR018','name':'Himachal Pradesh'},
    {'id':'CIR018','name':'Bihar & Jharkhand'},
    {'id':'CIR020','name':'Orissa'},
    {'id':'CIR021','name':'Assam'},
    {'id':'CIR022','name':'Northeast'},
    {'id':'CIR023','name':'J&K'},
    {'id':'CIR024','name':'Uttaranchal'}
    ];

    $scope.submitForm = function() {
        $scope.loader = true;
        $scope.successMsg = false;
    	$scope.errorloader = false;

        appService.saveNewCustomer(appToken, $scope.form, function(response) {
            $log.info(response);
            if (response.isSuccessful == true) {
                $scope.loader = false;
                $scope.successMsg = true;
                $scope.errorloader = false;
                $timeout(function(){
                    $window.location.reload();
                },5000);
            	
            } else if (response.isSuccessful == false && response.msgDesc == 'Token is Not Valid') {
                //$log.log(response.msgDesc);
                if ($scope.loggedIn == 1) {
                    $scope.invisible = !s; //!authService.invisible;
                    $cookieStore.put('invisible', !s);
                    $cookieStore.put('userloggedIn', 0);
                    authService.clearCredentials();
                    $window.alert('Session Expired ! Please login again.');
                    $state.go('home');

                } else {
                    $window.alert('Session Expired ! Click OK to start session again');
                    $state.go('home');
                }
            }else {
                $scope.loader = false;
                $scope.successMsg = false;
                $scope.errorloader = true;
            }
        });
    };
}]);
