t24App.controller('headerCtrl', ['$scope','$state','appService','$rootScope','authService','$cookieStore', function($scope,$state,appService,$rootScope,authService,$cookieStore){
	//console.log('headerCtrl');
	//console.log($rootScope.globals.current);
	//console.log(authService);
  var s =  $cookieStore.get('invisible');
	$scope.invisible = s;//authService.invisible;
//	console.log($scope.invisible);

	$scope.logout = function(){
		$scope.invisible = !s;//!authService.invisible;
    	$cookieStore.put('invisible',!s);
    	$cookieStore.put('userloggedIn',0);
    	authService.clearCredentials();
		$state.go('home');
	};

	$(function() {
	    $('.nav a').on('click', function(){ 
	        if($('.navbar-toggle').css('display') !='none'){
	            $(".navbar-toggle").trigger( "click" );
	        }
	    });
	});

}]);
