t24App.controller('datacardSelectRechargeCtrl', ['$scope','appService','$log','$stateParams','$state', function($scope,appService,$log,$stateParams,$state){
	console.log('datacardSelectRechargeCtrl');
    $scope.currentPage = 0;
    $scope.pageSize = 4;
    $scope.data = [];
    $scope.numberOfPages=function(apiData){
        return Math.ceil(apiData.length/$scope.pageSize);                
    }
   
  console.log($stateParams.mobileNo);
  $scope.loadGetStvList = function(){
    appService.getStvList('F3E630A9AEAAC2CB186516AB018ACFB1A1BE7902C0BAFB48194E1CE8260F1A73F676CBC063E52B80','T24',function(response){
      //$log.info(response.data);
      var stvLt = response.data;
      var stvList = [];
      for (var i = 0; i < stvLt.length; i++ ) {
          if (i % 2 == 0) stvList.push([]);
          stvList[stvList.length-1].push(stvLt[i]);
      }
      for (var i=0; i<stvLt.length; i++) {
      // $scope.data.push("Item "+i);
        $scope.stvList = stvList; 
      }

         
      console.log( $scope.stvList );   
    });	
  };
  $scope.loadGetStvList();

  $scope.getAmount = function(amount){
    console.log(amount);
    $state.go('datacardCoupons', {'datacardnumber':$stateParams.datacardnumber,'rechargeType':$stateParams.rechargeType,'operator':$stateParams.operator,'amount':amount});
  };

}]);
